mapper.select:
SELECT id,softwareName,APKName,softwareSize,(SELECT valueName FROM data_dictionary where typeCode='APP_FLATFORM' AND valueId=`flatformId`)as v_flatformId,(SELECT categoryName FROM app_category WHERE id=categoryLevel1)as v_categoryLevel1,(SELECT categoryName FROM app_category WHERE id=categoryLevel2)as v_categoryLevel2,(SELECT categoryName FROM app_category WHERE id=categoryLevel3)as v_categoryLevel3,(SELECT valueName FROM data_dictionary where typeCode='APP_STATUS' AND valueId=`status`)as v_status,downloads,(SELECT versionInfo FROM app_version where app_version.id=app_info.versionId AND app_info.id=app_version.appId)as v_versionid,status FROM app_info where devId=#{devId}

    and softwareName like concat('%',#{softwareName},'%')


  and status=#{status}


  and flatformId=#{flatformId}


  and categoryLevel1=#{categoryLevel1}


  and categoryLevel2=#{categoryLevel2}


  and categoryLevel3=#{categoryLevel3}

SELECT id,softwareName,APKName,softwareSize,(SELECT valueName FROM data_dictionary where typeCode='APP_FLATFORM' AND valueId=`flatformId`)as v_flatformId,(SELECT categoryName FROM app_category WHERE id=categoryLevel1)as v_categoryLevel1,(SELECT categoryName FROM app_category WHERE id=categoryLevel2)as v_categoryLevel2,(SELECT categoryName FROM app_category WHERE id=categoryLevel3)as v_categoryLevel3,(SELECT valueName FROM data_dictionary where typeCode='APP_STATUS' AND valueId=`status`)as v_status,downloads,(SELECT versionInfo FROM app_version where app_version.id=app_info.versionId AND app_info.id=app_version.appId)as v_versionid,status,
supportrom,interfacelanguage,appinfo
 FROM app_info where id=#{id}
